// components/NewPlayerForm.jsx
import React, { useState } from 'react';
import { createPlayer } from '..';

const NewPlayerForm = ({ navigate }) => {
    const [newPlayer, setNewPlayer] = useState({ name: '', owner: '', team: '' });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setNewPlayer((prev) => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const createdPlayer = await createPlayer(newPlayer);
        if (createdPlayer) {
            navigate.pish(`/players/${createdPlayer.id}`);
        } else {
            alert('Error creating player.');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>The NewPlayerForm component</h2>
            <label>
                Name:
                <input type='text' name='name' value={newPlayer.name} onChange={handleChange} />
            </label>
            <label>
                Owner:
                <input type='text' name='owner' value={newPlayer.owner} onChange={handleChange} />
            </label>
            <label>
                Team:
                <input type='text' name='team' value={newPlayer.team} onChange={handleChange} />
            </label>
            <button type='submit'>Add Player</button>
        </form>
    );
};

export default NewPlayerForm;
