// components/SinglePlayer.jsx
import React, { useState, useEffect } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { fetchPlayerById, deletePlayer } from '..';

const SinglePlayer = () => {
    const { id } = useParams();
    const [player, setPlayer] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const selectedPlayer = await fetchPlayerById(id);

                if (selectedPlayer) {
                    setPlayer(selectedPlayer);
                } else {
                    console.error('No data received for the selected player ID.');
                }
            } catch (error) {
                console.error('Error fetching player by ID:', error);
            }
        };

        fetchData();
    }, [id]);

    const handleDelete = async () => {
        try {
            const success = await deletePlayer(id);
            if (success) {
                navigate('/'); // Use navigate instead of history.push
            } else {
                alert('Error deleting player.');
            }
        } catch (error) {
            console.error('Error deleting player:', error);
            alert('Error deleting player.');
        }
    };

    return (
        <div>
            <h2>The SinglePlayer component</h2>
            {player && (
                <div>
                    <img src={player.imageUrl} width="500" height="600" alt="Player" /> 
                    <h4>{player.name}</h4>
                    <p>Owner: {player.owner}</p>
                    <p>Team: {player.team}</p>
                    <button onClick={handleDelete}>Delete Player</button>
                </div>
            )}
            <Link to='/'>Back to All Players</Link>
        </div>
    );
};

export default SinglePlayer;
